mapper.select:
SELECT * FROM V_BOARD
WHERE BOARD_TYPE = #{boardType}
ORDER BY BOARD_NO DESC

SELECT * FROM V_BOARD
WHERE BOARD_NO = #{boardNo}
AND BOARD_STATUS = 'Y'

mapper.update:
UPDATE BOARD SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD SET
BOARD_STATUS = 'N'
WHERE BOARD_NO = #{boardNo}
